package javaassignment;

public class NumberPattern7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count=0;
		for(int i=0;i<=5;i++)
		{
			if(i%2==0)
			{
				for(int j=1;j<=3;j++)
				{
					count=count+1;
					System.out.print(count +" ");
				}
				
				
				}
			else {
				
			}
			System.out.println();
			}
		}

	}

