package javaassignment;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a=20;
        int b=64;
        int res;
        String sy="*";
        switch (sy)
        {
        case "+": res=a+b;
        System.out.println(res);
        break; 
        
        
        case "-": res=a-b;
        System.out.println(res);
        break; 
        
        
        case "*": res=a*b;
        System.out.println(res);
        break; 
        }
		
		
		
		
	}

}
